bool allMachineBusy(){
    for (y =0; y<MACHINES;++y){
        if(!machineBusy[y]){
            return false;
        }
    }
    return true;
}




void chooseWinner(){
    int x,lowestBid, secondLowest, count;
    int lowestBidPrice= 10000;

    for(x = 0; x<MACHINES;x++){
        if(!machineBusy[x] ){
            count++;
            index = x;
        }
    }
    //if only one machine is busy then give the task to that machine others find the second lowest bidder.
    if(count != 1){
        //first loop finds lowest
        for (x = 0; x<MACHINES; x++){
            if(machineBids[x]<lowestBidPrice){
                lowestBidPrice = machineBids[x];
                lowestBid = x;
            }  
        }
        //second loop finds second lowest
        for (x = 0; x<MACHINES; x++){
            if(x!=lowestBid && machineBids[x] < machineBids[secondLowest]){
                secondLowest = x;
                index = x;
            }
        }
        secondPriceBid = machineBids[index];
        index = lowestBid;
    }

    machineWonAuction = index;
    machineBusy[index]=true;
    systemBusy = allMachineBusy(); 
    machineTaskListIndex[index]=distributorIndex;
    assignedTask[index] =  taskList[distributorIndex];
    distributorIndex++;
}

void resetAution(){
    int x; 

    for( x=0;x<MACHINES;x++){
        machineBids[x]=10000;  
    }

}

