template.declaration:
// Place local declarations here.
int distributorIndex =  0;
int i,y;
bool selected;
bool systemBusy; 
bool allMachineBusy(){
    for (y =0; y<MACHINES;++y){
        if(!machineBusy[y]){
            return false;
        }
    }
    return true;
}
void delecateTask(){
    selected = false;
    for (i = 0; i<MACHINES && !selected;++i) {
        if(!machineBusy[i]){
            machineBusy[i]= true ;
            assginedTask[i] = distributorIndex;
            distributorIndex++;
            selected = true;

        }
    }
    systemBusy = allMachineBusy(); 
}

clock time;
const int taskToCreate = 10;
int i;
int index= 0;
void createTask(int DL, int PU){
    taskList[index].deadLine=DL;
    taskList[index].processingUnits=PU;
    index++;
    numberOfTaskInQueue++;
}

